//This program is to display default value of primitive data types.
/*Primitive data types are of 8 types.
 * 1. Byte
 * 2. Short
 * 3. Int
 * 4. Long
 * 5. Float
 * 6. Double
 * 7. Boolean
 * 8. Char 
 */

package day1Problems;

public class DefaultValueofPrimitiveDataType {
	
	static byte a;
	static short b;
	static int c;
	static long d;
	static float e;
	static double f;
	static boolean g;
	static char h;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Default value of Byte is "+a);
		System.out.println("Default value of Short is "+b);
		System.out.println("Default value of Int is "+c);
		System.out.println("Default value of Long is "+d);
		System.out.println("Default value of Float is "+e);
		System.out.println("Default value of Double is "+f);
		System.out.println("Default value of Boolean is "+g);
		System.out.println("Default value of Char is "+h);
		

	}

}
